import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [npcs, setNpcs] = useState([]);
  const [worldEvents, setWorldEvents] = useState([]);
  const [currentEventIndex, setCurrentEventIndex] = useState(0);
  const [isAutoRunning, setIsAutoRunning] = useState(false);
  const [timerInterval, setTimerInterval] = useState(15);
  const [customEvent, setCustomEvent] = useState('');
  const [showConfig, setShowConfig] = useState(false);
  const [apiConfig, setApiConfig] = useState({
    apiKey: '',
    apiUrl: 'https://api.cerebras.ai/v1/chat/completions',
    modelName: 'llama3.1-70b'
  });
  const intervalRef = useRef(null);

  // Load default data on component mount
  useEffect(() => {
    loadDefaultData();
  }, []);

  const loadDefaultData = async () => {
    try {
      // Load NPCs
      const npcResponse = await fetch('/dataset/npcs_100.json');
      const npcData = await npcResponse.json();
      setNpcs(npcData.npcs || []);

      // Load World Events
      const eventsResponse = await fetch('/world_events.json');
      const eventsData = await eventsResponse.json();
      setWorldEvents(eventsData.world_events || []);
    } catch (error) {
      console.error('Error loading default data:', error);
      // Fallback to sample data if files not found
      loadSampleData();
    }
  };

  const loadSampleData = () => {
    // Sample NPCs based on the schema
    const sampleNpcs = [
      {
        id: "stark_001",
        name: "Ser Gareth of House Stark",
        house: "Stark",
        age: 34,
        gender: "male",
        role: "knight",
        traits: {
          honor: 82,
          loyalty: 88,
          intelligence: 65,
          emotional_intelligence: 70,
          evil: 12,
          selflessness: 75,
          ambition: 35,
          combat_skill: 80,
          political_cunning: 45,
          religious_devotion: 65
        },
        relationships: [
          { npc_id: "stark_002", relationship_type: "family", strength: 90 },
          { npc_id: "stark_015", relationship_type: "ally", strength: 75 }
        ],
        current_behavior: "Patrolling the borders with quiet determination",
        location: "Winterfell",
        status: "alive"
      }
    ];

    const sampleEvents = [
      {
        event_id: 1,
        title: "Lord Jon Arryn of House Arryn dies mysteriously",
        description: "The Hand of the King dies suddenly, creating a power vacuum in King's Landing",
        political_context: {
          house_stark: "Ned Stark of House Stark reluctantly accepts Hand position, honor compels duty to realm"
        },
        cascading_effects: ["Power vacuum in King's Landing", "Stark family travels south"]
      }
    ];

    setNpcs(sampleNpcs);
    setWorldEvents(sampleEvents);
  };

  const updateNPCWithAI = async (npc, event) => {
    const { apiKey, apiUrl, modelName } = apiConfig;

    if (!apiKey || apiKey.trim() === '') {
      console.warn('Cerebras API key not configured. Using fallback simulation.');
      return simulateNPCUpdate(npc, event);
    }

    const prompt = `You are an expert AI agent simulating NPC evolution in a Game of Thrones universe. Update the NPC based on the world event, ensuring changes align with Westerosi lore, politics, and character archetypes. Use the empirical formula: NPC Persona (with current behavior) × World Event = New Behavior + Change in Persona.

NPC Data (JSON schema-compliant):
${JSON.stringify(npc, null, 2)}

World Event:
${event.title} - ${event.description}

Reason step by step:
1. Assess impact: How does the event affect this NPC's house, role, location, status, traits, and relationships? Factor in traits (e.g., high evil amplifies malice, low loyalty risks defection).
2. New Behavior: Craft a concise natural language description of updated mood/actions, grounded in lore.
3. Persona Changes: Adjust traits by -20 to +20 max (subtle shifts); modify relationship strengths by -30 to +30 or add/remove if logical; update location/status only if directly implied.
4. Consistency: Avoid drastic overhauls—events ripple, not revolutionize. Preserve JSON schema integrity.

Output ONLY the updated NPC JSON. No extra text.`;

    try {
      const response = await axios.post(apiUrl, {
        model: modelName,
        messages: [
          {
            role: "system",
            content: "You are an expert Game of Thrones NPC simulation engine. Always respond with valid JSON only."
          },
          {
            role: "user",
            content: prompt
          }
        ],
        temperature: 0.7,
        max_tokens: 2000,
        stream: false
      }, {
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json'
        }
      });

      const aiResponse = response.data.choices[0].message.content;

      // Parse the AI response as JSON
      try {
        const updatedNpc = JSON.parse(aiResponse);
        // Validate that essential fields are preserved
        if (updatedNpc.id && updatedNpc.name && updatedNpc.house) {
          return updatedNpc;
        } else {
          throw new Error('Invalid NPC structure returned by AI');
        }
      } catch (parseError) {
        console.error('Failed to parse AI response as JSON:', parseError);
        console.log('AI Response:', aiResponse);
        return simulateNPCUpdate(npc, event);
      }
    } catch (error) {
      console.error('Error calling Cerebras API:', error);
      return simulateNPCUpdate(npc, event);
    }
  };

  const simulateNPCUpdate = (npc, event) => {
    // Fallback simulation when AI is not available
    const updatedNpc = { ...npc };

    // Simulate trait changes based on event
    if (event.title.includes('dies') || event.title.includes('executed')) {
      updatedNpc.traits.honor = Math.max(0, Math.min(100, updatedNpc.traits.honor + (Math.random() * 20 - 10)));
      updatedNpc.traits.loyalty = Math.max(0, Math.min(100, updatedNpc.traits.loyalty + (Math.random() * 15 - 5)));
      updatedNpc.current_behavior = `Reacting to recent tragic events with ${updatedNpc.traits.honor > 70 ? 'righteous anger' : 'calculated caution'}`;
    } else if (event.title.includes('war') || event.title.includes('battle')) {
      updatedNpc.traits.ambition = Math.max(0, Math.min(100, updatedNpc.traits.ambition + (Math.random() * 15 - 5)));
      updatedNpc.current_behavior = `Preparing for the conflicts ahead with ${updatedNpc.traits.ambition > 70 ? 'eager anticipation' : 'cautious planning'}`;
    } else {
      updatedNpc.traits.intelligence = Math.max(0, Math.min(100, updatedNpc.traits.intelligence + (Math.random() * 10 - 5)));
      updatedNpc.current_behavior = `Adapting to changing circumstances with ${updatedNpc.traits.intelligence > 70 ? 'strategic thinking' : 'careful observation'}`;
    }

    return updatedNpc;
  };

  const processNextEvent = async () => {
    if (currentEventIndex >= worldEvents.length) {
      setIsAutoRunning(false);
      return;
    }

    const currentEvent = worldEvents[currentEventIndex];
    const updatedNpcs = [];

    for (const npc of npcs) {
      const updatedNpc = await updateNPCWithAI(npc, currentEvent);
      updatedNpcs.push(updatedNpc);
    }

    setNpcs(updatedNpcs);
    setCurrentEventIndex(prev => prev + 1);
  };

  const startAutoRun = () => {
    setIsAutoRunning(true);
    intervalRef.current = setInterval(() => {
      processNextEvent();
    }, timerInterval * 1000);
  };

  const stopAutoRun = () => {
    setIsAutoRunning(false);
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
  };

  const sendCustomEvent = async () => {
    if (!customEvent.trim()) return;

    const event = {
      event_id: 999,
      title: customEvent,
      description: "Custom event triggered by user",
      political_context: {},
      cascading_effects: []
    };

    const updatedNpcs = [];
    for (const npc of npcs) {
      const updatedNpc = await updateNPCWithAI(npc, event);
      updatedNpcs.push(updatedNpc);
    }

    setNpcs(updatedNpcs);
    setCustomEvent('');
  };

  const formatNPCDisplay = (npc) => {
    const traits = npc.traits;
    const relationships = npc.relationships || [];

    return (
      <div key={npc.id} className="npc-card">
        <div className="npc-header">
          <span className="npc-id">id: {npc.id}</span>
          <span className="npc-name">name: {npc.name}</span>
        </div>
        <div className="npc-basic-info">
          🏠 {npc.house} 🔞{npc.age} {npc.gender === 'male' ? '👨' : '👩'}{npc.gender.charAt(0).toUpperCase()} 💼 {npc.role}
        </div>
        <div className="npc-traits">
          Honor: {traits.honor} Loyalty: {traits.loyalty} IQ: {traits.intelligence} EQ: {traits.emotional_intelligence} Evil: {traits.evil} Selfless: {traits.selflessness} Ambition: {traits.ambition} Combat: {traits.combat_skill} Political: {traits.political_cunning} Religion: {traits.religious_devotion}
        </div>
        <div className="npc-relationships">
          {relationships.map((rel, idx) => (
            <div key={idx}>
              👨‍👦 {rel.npc_id} {rel.relationship_type} Str{rel.strength}
            </div>
          ))}
        </div>
        <div className="npc-location">🌍 {npc.location}</div>
        <div className="npc-status">🟢 {npc.status}</div>
        <div className="npc-behavior">
          Current_Behavior: {npc.current_behavior}
        </div>
      </div>
    );
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>NPC Simulation Arena</h1>
        <div className="header-buttons">
          <button onClick={loadDefaultData} className="load-btn">
            Load World Quest
          </button>
          <button onClick={loadDefaultData} className="load-btn">
            Load Persona
          </button>
        </div>
      </header>

      <div className="main-content">
        <div className="left-panel">
          <h2>NPCs ({npcs.length})</h2>
          <div className="npc-list">
            {npcs.map(npc => formatNPCDisplay(npc))}
          </div>
        </div>

        <div className="right-panel">
          <div className="world-events-section">
            <h3>World Events</h3>
            <div className="current-event">
              {worldEvents[currentEventIndex] && (
                <div className="event-card">
                  <h4>Event #{worldEvents[currentEventIndex].event_id}</h4>
                  <p><strong>{worldEvents[currentEventIndex].title}</strong></p>
                  <p>{worldEvents[currentEventIndex].description}</p>
                </div>
              )}
            </div>

            <div className="event-controls">
              <div className="timer-controls">
                <label>
                  Auto Event Timer: 
                  <input 
                    type="number" 
                    value={timerInterval} 
                    onChange={(e) => setTimerInterval(parseInt(e.target.value))}
                    min="5"
                    max="300"
                  /> seconds
                </label>
              </div>

              <div className="control-buttons">
                {!isAutoRunning ? (
                  <button onClick={startAutoRun} className="start-btn">
                    ▶️ Start Auto Events
                  </button>
                ) : (
                  <button onClick={stopAutoRun} className="stop-btn">
                    ⏸️ Pause Auto Events
                  </button>
                )}

                <button onClick={processNextEvent} className="next-btn">
                  ⏭️ Force Next Event
                </button>
              </div>

              <div className="custom-event">
                <input 
                  type="text" 
                  placeholder="Send custom event..."
                  value={customEvent}
                  onChange={(e) => setCustomEvent(e.target.value)}
                  className="custom-event-input"
                />
                <button onClick={sendCustomEvent} className="send-btn">
                  📤 Send Event
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
