import React from 'react';
import LoadingOverlay from '../LoadingOverlay/LoadingOverlay';
import './WorldEvents.css';

const WorldEvents = ({
  worldEvents,
  currentEventIndex,
  isAutoRunning,
  timerInterval,
  customEvent,
  isProcessingEvent,
  onTimerIntervalChange,
  onManualStart,
  onStartAutoRun,
  onStopAutoRun,
  onProcessNextEvent,
  onCustomEventChange,
  onSendCustomEvent
}) => {
  return (
    <div className="right-panel">
      <div className="world-events-section">
        <h3>World Events</h3>

        <div className="event-controls">
          <div className="timer-controls">
            <label>
              Auto Timer: 
              <input 
                type="number" 
                value={timerInterval} 
                onChange={(e) => onTimerIntervalChange(parseInt(e.target.value))}
                min="5"
                max="300"
              /> sec
            </label>
          </div>

          <div className="control-buttons">
            <button 
              onClick={onManualStart}
              className="manual-start-btn"
              disabled={currentEventIndex !== 0}
            >
              üöÄ Manual Start Event
            </button>

            {!isAutoRunning ? (
              <button onClick={onStartAutoRun} className="start-btn">
                ‚ñ∂Ô∏è Auto Events
              </button>
            ) : (
              <button onClick={onStopAutoRun} className="stop-btn">
                ‚è∏Ô∏è Pause Auto
              </button>
            )}

            <button onClick={onProcessNextEvent} className="next-btn">
              ‚è≠Ô∏è Next Event
            </button>
          </div>

          <div className="custom-event">
            <input 
              type="text" 
              placeholder="Custom event..."
              value={customEvent}
              onChange={(e) => onCustomEventChange(e.target.value)}
              className="custom-event-input"
            />
            <button 
              onClick={onSendCustomEvent} 
              className="send-btn"
              disabled={!customEvent.trim()}
            >
              üì§ Send
            </button>
          </div>
        </div>

        <div className="events-stack" style={{ position: 'relative' }}>
          <LoadingOverlay 
            isVisible={isProcessingEvent} 
            message="Executing World Event..."
          />

          {worldEvents.map((event, index) => {
            let statusClass = 'event-pending';
            if (index < currentEventIndex) {
              statusClass = 'event-completed';
            } else if (index === currentEventIndex) {
              statusClass = 'event-running';
            }

            return (
              <div key={event.event_id} className={`event-card-stack ${statusClass}`}>
                <div className="event-header">
                  <span className="event-number">#{event.event_id}</span>
                  <span className="event-status-indicator">
                    {index < currentEventIndex ? '‚úÖ' : index === currentEventIndex ? 'üîÑ' : '‚è≥'}
                  </span>
                </div>
                <div className="event-title">{event.title}</div>
                <div className="event-description">{event.description}</div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default WorldEvents;
